import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
//import "../css/Library.css";
import "../css/GameInfo.css";

const apiKey = '0fbb76c073e64b8a919b4c0f0cf05a0b';

function GameInfo() {
  const [game, setGame] = useState({});
  const { id } = useParams();


  useEffect(() => {
    fetch(`https://rawg.io/api/games/${id}?key=${apiKey}`)
      .then(res => res.json())
      .then(data => setGame(data))
      .catch(error => console.error('Error:', error));
  }, [id]);

  if (!game) {
    return <div>Loading...</div>;
  }

  return (
    <div class="Info-container">
        <div class="gameContainer">
        <img src={game.background_image} alt={game.name} class="gameSplashart" />
        <h1>{game.name}</h1>
      <h4>Rating: {game.rating}</h4>
      <h4>Released: {game.released}</h4>
      <h2>Description</h2>
      <p>Description: {game.description_raw}</p>
      <h2>Publisher</h2>
      <p>Description: {game.publishers}</p>
    </div>

   
    
  
</div>

    
    
  );
}

export default GameInfo;
