import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Filter from '../menus/Filter';
import "../css/Library.css"; // Import CSS file

const apiKey = '0fbb76c073e64b8a919b4c0f0cf05a0b';

function Library() {
  const [games, setGames] = useState([]);
  const [filteredGames, setFilteredGames] = useState([]);
  const [page, setPage] = useState(1); // Initialize page state with 1
  const [selectedOption, setSelectedOption] = useState('all');

  function handleFilterChange(value) {
    setSelectedOption(value);
    // Add code to update filtered data here
  }

  useEffect(() => {
    const count=1000;
    fetch(`https://api.rawg.io/api/games?page=${page}&key=${apiKey}&metacritic=50,100&ordering=-rating&ratings_count_min=1000&exclude_additions=true`)
      .then(res => res.json())
      .then(data => {
        setGames(data.results);
      })
      .catch(error => console.error('Error:', error));
  }, [page]);


  const handlePageChange = (value) => {
    setPage(Math.max(1, page + value)); // Set minimum value of 1
  }

  function handleClick(currentCheckbox) {
    var checkboxes = document.getElementsByName("filters");
    for (var i = 0; i < checkboxes.length; i++) {
      if (checkboxes[i] !== currentCheckbox) {
        checkboxes[i].checked = false;
      }
    }
  }

  return (
    <div>

<button id="filter-button" class="button">Filter</button>
<div id="filter-container" class="filters">
  <ul class="filters__list">
    <li>
      <input id="f1" type="checkbox" name="filters" onclick="handleClick(this)" />
      <label for="f1">Filter 1</label>
    </li>
    <li>
      <input id="f2" type="checkbox" name="filters" onclick="handleClick(this)" />
      <label for="f2">Filter 2</label>
    </li>
    <li>
      <input id="f3" type="checkbox" name="filters" onclick="handleClick(this)" />
      <label for="f3">Filter 3</label>
    </li>
    <li>
      <input id="f4" type="checkbox" name="filters" onclick="handleClick(this)" />
      <label for="f4">Filter 4</label>
    </li>
    <li>
      <input id="f5" type="checkbox" name="filters" onclick="handleClick(this)" />
      <label for="f5">Filter 5</label>
    </li>
  </ul>
</div>



      

      <div className="game-container"> {/* Use a container to hold each game */}
        {games.map((game) => (
          <Link to={`/GameInfo/${game.id}`} key={game.id} game={game}>
            <div className="game-box"> {/* Use a box for each game */}
            <img src={game.background_image} alt={game.name} />
            <h2>{game.name}</h2>
            <p>Rating: {game.rating}</p> {/* Display the rating instead of the release date */}
          </div>
          </Link>
        ))}
      </div>
    </div>
  );

}


export default Library;
