import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
//import "../css/Library.css";
import "../css/GameInfo.css";

const apiKey = '0fbb76c073e64b8a919b4c0f0cf05a0b';

function GameInfo() {
  const [game, setGame] = useState({});
  const { id } = useParams();

  useEffect(() => {
    fetch(`https://rawg.io/api/games/${id}?key=${apiKey}`)
      .then(res => res.json())
      .then(data => setGame(data))
      .catch(error => console.error('Error:', error));
  }, [id]);

  return (
    <div class="Info-container">
        <div class="gameContainer">
        <img src={game.background_image} alt={game.name} class="gameSplashart" />
        <h1>{game.name}</h1>
      <p>Rating: {game.rating}</p>
      <p>Released: {game.released}</p>
      <h2>Description</h2>
      <p>Description: {game.description_raw}</p>
    </div>

    
    <div className="screenshots-container">
        {game.short_screenshots.map((screenshot) => (
          <img src={screenshot.image} alt={game.name} key={screenshot.id} />
        ))}

</div>
</div>

    
    
  );
}

export default GameInfo;
